        private void ValidateSig(LmsPublicKeyParameters lmsPub, byte[] sig, byte[] msg)
        {
            LmsSigner lmsSigner = new LmsSigner();

            lmsSigner.Init(false, lmsPub);

            Assert.IsTrue(lmsSigner.VerifySignature(msg, sig));
        }
