namespace Org.BouncyCastle.Pqc.Tests
{
    [TestFixture]
    public class MLDsaTest
    {
        private static readonly byte[] Message = Strings.ToByteArray("Hello World!");

        private SecureRandom m_random = null;

        [OneTimeSetUp]
        public void OneTimeSetUp()
        {
            m_random = new SecureRandom();
        }

        [OneTimeTearDown]
        public void OneTimeTearDown()
        {
            m_random = null;
        }

        [Test]
        public void HashMLDsaKatSig()
        {
            byte[] pubK = Hex.Decode("");
            byte[] privK = Hex.Decode("");
            byte[] msg = Hex.Decode("D81C4D8D734FCBFBEADE3D3F8A039FAA2A2C9957E835AD55B22E75BF57BB556AC8");
            byte[] s = Hex.Decode("");
            byte[] seed = Hex.Decode("7c9935a0b07694aa0c6d10e4db6b1add2fd81a25ccb148032dcd739936737f2d");
            byte[] genS;

            SecureRandom katRandom = new NistSecureRandom(
                Hex.Decode("061550234D158C5EC95595FE04EF7A25767F2E24CC2BC479D09D86DC9ABCFDE7056A8C266F9EF97ED08541DBD2E1FFA1"),
                null);

            var kpg = GeneratorUtilities.GetKeyPairGenerator("ML-DSA");
            kpg.Init(new MLDsaKeyGenerationParameters(katRandom, NistObjectIdentifiers.id_hash_ml_dsa_44_with_sha512));

            var kp = kpg.GenerateKeyPair();

            var pubInfo = SubjectPublicKeyInfoFactory.CreateSubjectPublicKeyInfo(kp.Public);
            DerBitString pubSeq = pubInfo.PublicKey;
            Assert.True(Arrays.AreEqual(pubSeq.GetOctets(), pubK));

            PrivateKeyInfo privInfo = PrivateKeyInfoFactory.CreatePrivateKeyInfo(kp.Private);
            Assert.True(Arrays.AreEqual(privInfo.PrivateKey.GetOctets(), seed));

            // TODO[pqc] Can't create deterministic signers via SignerUtilities yet
            ISigner sigDet = new HashMLDsaSigner(MLDsaParameters.ml_dsa_44_with_sha512, deterministic: true);
            ISigner sigRnd = SignerUtilities.GetSigner(NistObjectIdentifiers.id_hash_ml_dsa_44_with_sha512);

            sigDet.Init(forSigning: true, kp.Private);
            sigDet.BlockUpdate(msg, 0, msg.Length);
            genS = sigDet.GenerateSignature();
            Assert.True(Arrays.AreEqual(s, genS));

            sigDet.Init(forSigning: false, kp.Public);
            sigDet.BlockUpdate(msg, 0, msg.Length);
            Assert.True(sigDet.VerifySignature(s));

            // check randomisation
            var withRandom = new ParametersWithRandom(kp.Private, m_random);

            sigRnd.Init(forSigning: true, withRandom);
            sigRnd.BlockUpdate(msg, 0, msg.Length);
            genS = sigRnd.GenerateSignature();
            Assert.False(Arrays.AreEqual(s, genS));

            sigRnd.Init(forSigning: false, kp.Public);
            sigRnd.BlockUpdate(msg, 0, msg.Length);
            Assert.True(sigRnd.VerifySignature(genS));

            // test using ml-dsa-44 for the key, should be the same.

            katRandom = new NistSecureRandom(
                Hex.Decode("061550234D158C5EC95595FE04EF7A25767F2E24CC2BC479D09D86DC9ABCFDE7056A8C266F9EF97ED08541DBD2E1FFA1"),
                null);

            kpg.Init(new MLDsaKeyGenerationParameters(katRandom, NistObjectIdentifiers.id_ml_dsa_44));

            kp = kpg.GenerateKeyPair();

            sigDet.Init(forSigning: true, kp.Private);
            sigDet.BlockUpdate(msg, 0, msg.Length);
            genS = sigDet.GenerateSignature();
            Assert.True(Arrays.AreEqual(s, genS));

            sigDet.Init(forSigning: false, kp.Public);
            sigDet.BlockUpdate(msg, 0, msg.Length);
            Assert.True(sigDet.VerifySignature(s));
        }

        [Test]
        public void HashMLDsaKatSigWithContext()
